import { MagicString } from 'xajs/lang'
// Create a magic string for code manipulation
const code = new MagicString('function hello() { return "world" }')
// Manipulate the code
code
.update(8, 13, 'greet')
.append('hello()')
console.log(code.toString())
// Output: function greet() { return "world" }hello()
